import React, { Component } from "react";
import "../css/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import Cookies from "universal-cookie";

const baseUrl = "http://localhost:3001/usuarios";
const cookies = new Cookies();

class Login extends Component {
  state = {
    form: {
      username: "",
      password: "",
    },
  };

  handleChange = async (e) => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
  };

  iniciarSesion = async () => {
    await axios
      .get(baseUrl, {
        params: {
          username: this.state.form.username,
          password: this.state.form.password
        }
      })
      .then((response) => {
        return response.data;
      })
      .then((response) => {
        if (response.length > 0) {
          const respuesta = response[0];
          cookies.set("id", respuesta.id, { path: "/" });
          cookies.set("primer_apellido", respuesta.primer_apellido, {
            path: "/",
          });
          cookies.set("segundo_apellido", respuesta.segundo_apellido, {
            path: "/",
          });
          cookies.set("nombre", respuesta.nombre, { path: "/" });
          cookies.set("username", respuesta.username, { path: "/" });
          alert(`Bienvenido ${respuesta.nombre} ${respuesta.primer_apellido}`);
          window.location.href="./menu";
        } else {
          alert("El usuario o la contrase침a no son correctos");
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };

  componentDidMount(){
    if(cookies.get('username')){
      window.location.href="./menu";
    }
  }

  render() {
    return (
      <div className="containerPrincipal">
        <div className="containerSecundario">
          <div className="form-group">
            <h1>Inicio de Sesion</h1>
            <input
              type="text"
              className="form-control"
              name="username"
              placeholder="usuario"
              onChange={this.handleChange}
            />
            <br />
            <input
              type="password"
              className="form-control"
              name="password"
              placeholder="contrase침a"
              onChange={this.handleChange}
            />
            <input type="checkbox" />
            <label htmlFor="">Recuerdame</label>
            <br />
            <button
              className="btn btn-primary"
              onClick={() => this.iniciarSesion()}
            >
              Iniciar Sesi칩n
            </button>
            <br />
            <p>?</p>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
